mapper.select:
SELECT meeting_number, meeting_title, date_format(meeting_insertdate, '%Y-%m-%d') meeting_insertdate, meeting_startdate, meeting_hits
FROM meeting
WHERE users_id = #{users_id}
ORDER BY meeting_number desc limit #{startNo}, 10;

SELECT COUNT(*)
FROM meeting
WHERE users_id = #{users_id}

SELECT favorites_number, users_id,  favorites_date,  meeting_number, meeting_title
FROM favorites NATURAL JOIN meeting
WHERE users_id = #{users_id}
ORDER BY favorites_number desc limit #{startNo}, 10;

SELECT COUNT(*)
FROM favorites
WHERE users_id = #{users_id}
